begin
wwv_flow_imp_shared.create_plugin(
 p_id=>wwv_flow_imp.id(42164603338149719)
,p_plugin_type=>'ITEM TYPE'
,p_name=>'TEAM_VOICE_RECORD_BUTTON'
,p_display_name=>'VoiceRecordButton'
,p_supported_component_types=>'APEX_APPLICATION_PAGE_ITEMS:APEX_APPL_PAGE_IG_COLUMNS'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('ITEM TYPE','TEAM_VOICE_RECORD_BUTTON'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'procedure render (',
'    p_item   in            apex_plugin.t_item,',
'    p_plugin in            apex_plugin.t_plugin,',
'    p_param  in            apex_plugin.t_item_render_param,',
'    p_result in out nocopy apex_plugin.t_item_render_result ) is',
'',
'',
'    l_html varchar2(32767);',
'     l_value clob;',
'     l_js varchar2(32767);',
'    begin',
' apex_plugin_util.debug_page_item (',
'    p_plugin    => p_plugin,',
'    p_page_item => p_item );',
'        ',
'        l_value := apex_session_state.get_clob(p_item.session_state_name);',
'',
'',
'        l_html := q''~',
'            <style> #%ITEM_NAME%_LABEL {display: none;}</style>',
'            <style>',
'                @keyframes VCB_blink {',
'                  0%, 100% { opacity: 1; }',
'                  50% { opacity: 0; }',
'                }',
'',
'                .VCB_blink {',
'                  animation: VCB_blink 1s linear infinite;',
'                }',
'            </style>',
'            <button onclick="return false;" name="%ITEM_NAME%_BUTTON" id="%ITEM_NAME%_BUTTON" %ATTRIBUTES% class="%ICON_CLASS% %CLASS%" >%LABEL%</button>',
'            <input type="hidden" id="%ITEM_NAME%" name="%ITEM_NAME%" value="">',
'            <audio id="audioPlayback_%ITEM_NAME%" controls style="display: none;"></audio>',
'        ~'';',
'',
'        l_html := replace(l_html,''%ITEM_NAME%'', p_item.name);',
'        l_html := replace(l_html,''%ATTRIBUTES%'', p_item.element_attributes);',
'        l_html := replace(l_html,''%ICON_CLASS%'', case when p_item.icon_css_classes is not null then ''fa '' end||p_item.icon_css_classes);',
'        l_html := replace(l_html,''%CLASS%'', p_item.element_css_classes);',
'        l_html := replace(l_html,''%LABEL%'', p_item.plain_label);',
'',
'        ',
'         l_js := q''~',
'            let mediaRecorder_%ITEM_NAME%;',
'            let audioChunks_%ITEM_NAME% = [];',
'            let silenceTimer_%ITEM_NAME%;',
'            let mediaStream_%ITEM_NAME%;',
'            const silenceDelay_%ITEM_NAME% = %ATTR01%; // Millisekunden',
unistr('            const threshold_%ITEM_NAME% = %ATTR02%; // Schwellenwert f\00FCr die Stilleerkennung'),
'            let isRecording_%ITEM_NAME% = false;       ',
'            let noSilenceDetection_%ITEM_NAME% = false;       ',
'',
'    ',
'',
'            const toggleRecordingButton_%ITEM_NAME% = document.getElementById(''%ITEM_NAME%_BUTTON'');',
'            const audioPlayback_%ITEM_NAME% = document.getElementById(''audioPlayback_%ITEM_NAME%'');        ',
'            ',
'',
'',
'            toggleRecordingButton_%ITEM_NAME%.addEventListener(''click'', (event) => {',
'              console.log(''Click Event:'',event);  ',
'              noSilenceDetection_%ITEM_NAME% = event.ctrlKey;',
'              if (isRecording_%ITEM_NAME%) {',
'                stopRecording_%ITEM_NAME%();                ',
'              } else {',
'                startRecording_%ITEM_NAME%();',
'              }',
'            });',
'',
'            function startRecording_%ITEM_NAME%() {',
'              navigator.mediaDevices.getUserMedia({ audio: true })',
'                .then(stream => {',
'                  mediaStream_%ITEM_NAME% = stream;',
'                  startSilence_%ITEM_NAME% = true;',
'                  const audioContext = new (window.AudioContext || window.webkitAudioContext)();',
'                  const mediaStreamSource = audioContext.createMediaStreamSource(stream);',
'                  const analyser = audioContext.createAnalyser();',
'                  analyser.fftSize = 512;',
'                  mediaStreamSource.connect(analyser);',
'',
'                  mediaRecorder_%ITEM_NAME% = new MediaRecorder(stream);',
'                  ',
'                  mediaRecorder_%ITEM_NAME%.start();',
'                  isRecording_%ITEM_NAME% = true;',
'                  //toggleRecordingButton_%ITEM_NAME%.textContent = ''Stop Recording'';',
'                  toggleRecordingButton_%ITEM_NAME%.classList.add(''fa-anim-flash'');',
'',
'',
'                  const bufferLength = analyser.frequencyBinCount;',
'                  const dataArray = new Uint8Array(bufferLength);',
'',
'                  const checkAudio = () => {',
'                    analyser.getByteFrequencyData(dataArray);',
'                    ',
'                    let sum = 0;',
'                    for (let i = 0; i < bufferLength; i++) {',
'                      sum += dataArray[i];',
'                    }',
'                    let average = sum / bufferLength;',
unistr('                    //volumeIndicator.textContent = `Volume: ${average.toFixed(2)}`; // Anzeige der durchschnittlichen Lautst\00E4rke'),
'',
'                    if (average < threshold_%ITEM_NAME% && !noSilenceDetection_%ITEM_NAME% ) { ',
'                         ',
'                      if (!silenceTimer && !startSilence_%ITEM_NAME%) {',
'                        silenceTimer = setTimeout(() => {',
'                          stopRecording_%ITEM_NAME%();',
'                        }, silenceDelay);',
'                      }',
'                    } else {',
'                      startSilence_%ITEM_NAME% = false;',
'                      clearTimeout(silenceTimer);',
'                      silenceTimer = null;',
'                    }',
'                  };',
'',
'                  mediaRecorder_%ITEM_NAME%.addEventListener(''dataavailable'', event => {',
'                    console.log(''push'');',
'                    audioChunks_%ITEM_NAME%.push(event.data);',
'                  });',
'',
'                  mediaRecorder_%ITEM_NAME%.addEventListener(''stop'', () => {',
'                    const audioBlob = new Blob(audioChunks_%ITEM_NAME%, { type: ''%ATTR03%'' });',
'                    //audioPlayback_%ITEM_NAME%.src = URL.createObjectURL(audioBlob);',
'                    if (''%ATTR04%'' != '''') {',
'                        audioPlayback_%ITEM_NAME%.src = ''%ATTR04%'';',
'                        setTimeout(()=>{audioPlayback_%ITEM_NAME%.play();},10);',
'                    }',
'                    audioChunks_%ITEM_NAME% = [];',
'                    audioContext.close();',
'                      const reader = new FileReader();',
'                      reader.readAsDataURL(audioBlob);',
'                      reader.onloadend = function() { ',
'                        apex.item(''%ITEM_NAME%'').setValue(reader.result);',
'                      }',
'                    ',
'                  });',
'',
unistr('                  // \00DCberpr\00FCfen Sie den Audiopegel alle 100 Millisekunden'),
'                  const audioCheckInterval = setInterval(checkAudio, 100);',
'',
'                  mediaRecorder_%ITEM_NAME%.addEventListener(''stop'', () => {',
'                    clearInterval(audioCheckInterval);',
unistr('                    //volumeIndicator.textContent = ''Volume: 0''; // Zur\00FCcksetzen der Lautst\00E4rkeanzeige'),
'                  });',
'                })',
'                .catch(error => {',
'                  console.error(''Error accessing the microphone'', error);',
'                });',
'            }',
'',
'            function stopRecording_%ITEM_NAME%() {',
'              if (mediaRecorder_%ITEM_NAME% && mediaRecorder_%ITEM_NAME%.state !== ''inactive'') {',
'                mediaRecorder_%ITEM_NAME%.stop();',
'                isRecording_%ITEM_NAME% = false;',
'                //toggleRecordingButton_%ITEM_NAME%.textContent = ''Start Recording'';',
'                toggleRecordingButton_%ITEM_NAME%.classList.remove(''fa-anim-flash'');',
'                clearTimeout(silenceTimer_%ITEM_NAME%);',
'                silenceTimer_%ITEM_NAME% = null;',
'',
'                 mediaStream_%ITEM_NAME%.getTracks().forEach((track) => {',
'                  track.stop();',
'                });',
'              }',
'            }    ',
'',
'             ',
'        ~'';',
'',
'          ',
'           l_js := replace(l_js,''%ITEM_NAME%'', p_item.name);',
'           l_js := replace(l_js,''%ATTR01%'', nvl(p_item.attribute_01,1000) );',
'           l_js := replace(l_js,''%ATTR02%'', nvl(p_item.attribute_02,10) );',
'           l_js := replace(l_js,''%ATTR03%'', nvl(p_item.attribute_03,''audio/ogg; codecs=opus''));',
'           l_js := replace(l_js,''%ATTR04%'', nvl(p_item.attribute_04,''''));',
'',
'           apex_javascript.add_onload_code(p_code => l_js);',
'',
'        -- If there''s an error, display it',
'        /*',
'        if p_param.is_error then',
'            l_html := l_html || ''<span class="apex_error">'' || apex_escape.html(p_param.error_message) || ''</span>'';',
'        end if;',
'*/',
'        -- Set the result',
'       htp.p(l_html);',
'',
'        -- Include additional attributes if necessary',
'        -- p_result.attribute_01 := ''some-value''; -- Example for additional attributes',
'',
'        -- Include additional JavaScript, CSS, or AJAX callbacks if necessary',
'        -- p_result.apex_javascript_code := ''...''; -- Example for including JavaScript',
'        -- p_result.apex_css_code := ''...''; -- Example for including CSS',
'        -- p_result.ajax_identifier := ''...''; -- Example for AJAX callbacks',
'  return;',
'end;'))
,p_default_escape_mode=>'HTML'
,p_api_version=>2
,p_render_function=>'render'
,p_standard_attributes=>'VISIBLE:FORM_ELEMENT:ICON:SESSION_STATE_CLOB'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_version_identifier=>'0.1'
);
